import axios from "axios";
import React, { useEffect, useState, useReducer } from "react";
import { useCategory } from "../../context/Category/CategoryContext";
import { initialState, reducer } from "../../reducers/reducer";
import CategoryCard from "./CategoryCard";

const CategorySection = () => {
  // const { state } = useCategory();
  const [state, dispatch] = useReducer(reducer, initialState);
  const [categories, setCategories] = useState([]);
  useEffect(() => {
    axios
      .get("/api/categories")
      .then((res) => {
        dispatch({ action: "SUCCESS", payload: res.data });
        console.log(res.data);
        setCategories(res.data.categories);
      })
      .catch((err) => {
        dispatch({ action: "FAILURE", payload: err.message });
        console.log(err);
      });
  }, []);
  return (
    <section className="section-category p-5  mr-0 pr-4 mt-0 pt-0">
      <h1 className="text-2xl mb-3">Shop By Category</h1>
      <div className="row gap-1 ">
        {/* {state.loading && <h1>Loading...</h1>} */}

        {categories.length ? (
          categories
            .slice(6)
            .map((category) => (
              <CategoryCard
                img={category.img[0]}
                title={category.categoryName}
                key={category._id}
              />
            ))
        ) : (
          <h1>No Categories</h1>
        )}
      </div>
    </section>
  );
};

export default CategorySection;
