import React, { useEffect } from "react";

const RatingBar = ({ ratings }) => {
  const [ratingBar, setRatingBar] = useState();

  const buildRatingBar = () => {
    const _ratingBar = "";
    for (let i = 0; i < ratings; i++) {
      _ratingBar += <span className="fa fa-star rating-active"></span>;
    }

    for (let i = 0; i < 5 - ratings - 1; i++) {
      _ratingBar += <span className="fa fa-star rating"></span>;
    }
    setRatingBar(_ratingBar);
  };
  useEffect(() => {
    buildRatingBar();
  }, [ratings]);
  return <div className="rating-bar">{ratingBar}</div>;
};

export default RatingBar;
