import React, { useEffect } from "react";

const RatingBar = ({ ratings }) => {
  const [ratingBar, setRatingBar] = useState();

  const buildRatingBar = () => {
    const _ratingBar = "";
    for (let i = 0; i < ratings; i++) {
      _ratingBar += <span className="fa fa-star rating-active"></span>;
    }

    for (let i = 0; i < 5 - ratings - 1; i++) {
      _ratingBar += <span className="fa fa-star rating"></span>;
    }
    setRatingBar(_ratingBar);
  };
  useEffect(() => {
    buildRatingBar();
  }, [ratings]);
  return (
    <Container>
      {[...Array(5)].map((item, index) => {
        const givenRating = index + 1;
        return (
          <label>
            <Radio
              type="radio"
              value={givenRating}
              onClick={() => {
                setRate(givenRating);
                alert(`Are you sure you want to give ${givenRating} stars ?`);
              }}
            />
            <Rating>
              <FaStar
                color={
                  givenRating < rate || givenRating === rate
                    ? "000"
                    : "rgb(192,192,192)"
                }
              />
            </Rating>
          </label>
        );
      })}
    </Container>
  );
};

export default RatingBar;
