import React, { useEffect, useState } from "react";
import { useFilter } from "../../context/FilterContext";

const FilterSection = ({ showFilter }) => {
  const [filters, setFilters] = useFilter();
  const [price, setPrice] = useState(8000);
  const handleCategories = (e) => {
    console.log("VALUE", e.target.value);
    !filters.categories.includes(e.target.value)
      ? setFilters((_filters) => {
          return {
            ..._filters,
            categories: [...filters.categories, e.target.value],
          };
        })
      : setFilters((_filters) => {
          return {
            ..._filters,
            categories: filters.categories.filter(
              (category) => category !== e.target.value
            ),
          };
        });
  };
  useEffect(() => {
    setFilters((_filters) => {
      return {
        ..._filters,
        price,
      };
    });
  }, [price]);

  return (
    <aside
      className={`sidebar col gap-2 overflow-y-auto ${
        showFilter ? "show" : ""
      }`}
    >
      <div className="pr-1 row justify-between">
        <h3 className="text-lg">Filter</h3>
        <h3 className="text-lg pointer">Clear</h3>
      </div>
      <div className="col gap-1">
        <h3 className="sidebar-title text-lg">Price</h3>
        <h3 className="price-range text-lg text-center">5k-20k</h3>
        <div className="silder-contaier  col gap-1 justify-start">
          <input
            type="range"
            min="5000"
            max="20000"
            className="slider"
            defaultValue="10000"
            id="slider"
            onChange={(e) => {
              setPrice(e.target.value);
            }}
          />
          <span id="price">
            â‚¹{price}
            {/* <span id="slider-value" className="slider-value text-light"></span> */}
          </span>
        </div>
      </div>
      <div className="col gap-1">
        <h3 className="sidebar-title text-lg">Category</h3>

        <label className="checkbox-container">
          Headphone
          <input
            type="checkbox"
            defaultValue="Headphone"
            onChange={handleCategories}
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          DJ Kit
          <input
            type="checkbox"
            defaultValue="DJ Kit"
            onChange={handleCategories}
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          Keyboard
          <input
            type="checkbox"
            defaultValue="Keyboard"
            onChange={handleCategories}
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          Studio Mic
          <input
            type="checkbox"
            defaultValue="Studio Mic"
            onChange={handleCategories}
          />
          <span className="checkmark"></span>
        </label>

        <label className="checkbox-container">
          Speaker
          <input
            type="checkbox"
            defaultValue="Speaker"
            onChange={handleCategories}
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          Midi Keyboard
          <input
            type="checkbox"
            defaultValue="Midi Keyboard"
            onChange={handleCategories}
          />
          <span className="checkmark"></span>
        </label>
      </div>
      <div className="col gap-1">
        <h3 className="sidebar-title  text-lg">Brand</h3>

        <label className="checkbox-container">
          Yamaha
          <input
            type="checkbox"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  brands: [...filters.brands, "Yamaha"],
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          Casio
          <input
            type="checkbox"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  brands: [...filters.brands, "Casio"],
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          Pioneer
          <input
            type="checkbox"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  brands: [...filters.brands, "Pioneer"],
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>

        <label className="checkbox-container">
          AudioTechnica
          <input
            type="checkbox"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  brands: [...filters.brands, "AudioTechnica"],
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          Sony
          <input
            type="checkbox"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  brands: [...filters.brands, "Yamaha"],
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
        <label className="checkbox-container">
          Behringer
          <input
            type="checkbox"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  brands: [...filters.brands, "Behringer"],
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
      </div>
      <div className=" col gap-1">
        <h3 className="sidebar-titletext-lg">Rating</h3>

        <label className="radio-container">
          4 star & above
          <input
            type="radio"
            name="rating"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  rating: 4,
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
        <label className="radio-container">
          3 star & above
          <input
            type="radio"
            name="rating"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  rating: 3,
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
        <label className="radio-container">
          2 star & above
          <input
            type="radio"
            name="rating"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  rating: 2,
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
        <label className="radio-container">
          1 star & above
          <input
            type="radio"
            name="rating"
            onChange={() =>
              setFilters((_filters) => {
                return {
                  ..._filters,
                  rating: 1,
                };
              })
            }
          />
          <span className="checkmark"></span>
        </label>
      </div>
      {/* <div className=" col gap-1">
        <h3 className="sidebar-title text-lg">Sort by</h3>

        <label className="radio-container">
          Newest Arrival
          <input type="radio" name="sortby"  />
          <span className="checkmark"></span>
        </label>
        <label className="radio-container">
          Price - Low to High
          <input type="radio" name="sortby" />
          <span className="checkmark"></span>
        </label>
        <label className="radio-container">
          Price - High to Low
          <input type="radio" name="sortby" />
          <span className="checkmark"></span>
        </label>
      </div> */}
    </aside>
  );
};

export default FilterSection;
