import axios from "axios";
import React, { useEffect, useState, createContext, useContext } from "react";
import { useProduct } from "./ProductsContext";
const FiltersContext = createContext();
const FiltersProvider = ({ children }) => {
  const [products, setProducts] = useProduct();
  const [filters, setFilters] = useState({
    price: 8000,
    categories: [],
    brands: [],
    rating: 4,
  });
  const handleFilterProp = (prop, value) => {
    !filters.prop.includes(value)
      ? setFilters((_filters) => {
          return {
            ..._filters,
            [prop]: [...filters.prop, value],
          };
        })
      : setFilters((_filters) => {
          return {
            ..._filters,
            [prop]: filters.prop.filter((_prop) => _prop !== value),
          };
        });
  };
  const handleCategories = (e) => {
    console.log("VALUE", e.target.value);
    handleFilterProp("categories", e.target.value);
    // !filters.categories.includes(e.target.value)
    //   ? setFilters((_filters) => {
    //       return {
    //         ..._filters,
    //         categories: [...filters.categories, e.target.value],
    //       };
    //     })
    //   : setFilters((_filters) => {
    //       return {
    //         ..._filters,
    //         categories: filters.categories.filter(
    //           (category) => category !== e.target.value
    //         ),
    //       };
    //     });
  };
  const handleBrands = (e) => {
    console.log("VALUE", e.target.value);
    handleFilterProp("brands", e.target.value);
    // !filters.brands.includes(e.target.value)
    //   ? setFilters((_filters) => {
    //       return {
    //         ..._filters,
    //         brands: [...filters.brands, e.target.value],
    //       };
    //     })
    //   : setFilters((_filters) => {
    //       return {
    //         ..._filters,
    //         brands: filters.brands.filter(
    //           (brands) => brands !== e.target.value
    //         ),
    //       };
    //     });
  };
  useEffect(() => {
    console.log("filter", filters);
    const _products = products.data.filter(
      (product) =>
        filters.brands.includes(product.brand) && filters.price <= product.price
    );
    setProducts({
      loading: false,
      data: _products,
      error: "",
    });
  }, [filters]);
  return (
    <FiltersContext.Provider
      value={[filters, setFilters, handleCategories, handleBrands]}
    >
      {children}
    </FiltersContext.Provider>
  );
};

const useFilter = () => useContext(FiltersContext);
export { useFilter, FiltersProvider };
