const initialState = {
  data: [],
  loading: true,
  erorr: "",
};
const categoryReducer = (state, action) => {
  switch (action.type) {
    case "FETCH_CATEGORIES":
      return {
        ...state,
        data: action.payload,
        loading: false,
      };
    case "FETCH_CATEGORIES_ERROR":
      return {
        ...state,
        error: action.payload,
        loading: false,
      };
    default:
      return state;
  }
};

export { initialState, categoryReducer };
