import React, { useEffect } from "react";

const RatingBar = ({ ratings }) => {
  const ratingBar = <span class="fa fa-star rating-active"></span>;
  const buildRatingBar = () => {
    for (let i = 0; i < ratings - 1; i++) {
      ratingBar += <span class="fa fa-star rating-active"></span>;
    }

    for (let i = 0; i < 5 - ratings - 1; i++) {
      ratingBar += <span class="fa fa-star rating"></span>;
    }
  };
  useEffect(() => {
    buildRatingBar();
  }, [ratings]);
  return <div class="rating-bar">{ratingBar}</div>;
};

export default RatingBar;
